import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];


export default function App() {

  /**add new friend state */
  const [showfriend, setShowFriend] = useState(false);
  function onClickFriendForm() {
    setShowFriend((show) => !show);
  }

  /**friends list state */
  const [friends, setFriends] = useState(initialFriends);





  return (
    <div className="app">
      <div className="sidebar">
        <Friendslist />
        {showfriend && <AddFriendForm />}
        <Button onClickFriendForm={onClickFriendForm} >{showfriend ? 'Close' : 'Add friend'}</Button>
      </div>
      <FormsSplitBill />
    </div>
  );
}


function Friendslist() {
  const friends = initialFriends;
  return (
    <ul>
      {friends.map(friend =>
        <Friend friend={friend} key={friend.id} />)}
    </ul>
  )
};

function Friend({ friend }) {
  return (
    <li>
      <img src={friend.image} alt={friend.name} />
      <h3>{friend.name}</h3>

      {friend.balance < 0 && <p className="red">You own {friend.name} ${Math.abs(friend.balance)}</p>}
      {friend.balance > 0 && <p className="green"> {friend.name} owes you ${friend.balance}</p>}
      {friend.balance === 0 && <p> You and {friend.name} are even</p>}
      <Button>Select</Button>
    </li>
  )
}

function AddFriendForm({ submitForm }) {

  const [friendname, setFriendName] = useState('');
  const [imageURL, setImageURL] = useState('https://i.pravatar.cc/48?u=118836');

  function handleSubmit(e) {
    e.preventDefault();

    if (!friendname || !imageURL) return;

    const id = crypto.randomUUID();
    const newFriend = {
      id,
      friendname,
      imageURL: `${imageURL}?=${id}`,
      balance: 0,

    };
    console.log(newFriend);

    setFriendName('');
    setImageURL('https://i.pravatar.cc/48?u=118836');
  }

  return (
    <form className="form-add-friend" onSubmit={handleSubmit}>
      <span>ðŸ‘­ Friend name </span>
      <input type="text"
        value={friendname}
        onChange={e => setFriendName(e.target.value)} />
      <span>ðŸŽ‘ image URL</span>
      <input type="text"
        value={imageURL}
        onChange={e => setImageURL(e.target.value)} />
      <Button>Add</Button>
    </form>
  )
}

function Button({ children, onClickFriendForm }) {
  return (
    <button className="button"
      onClick={onClickFriendForm}>{children}</button>
  )
}


function FormsSplitBill() {
  return (
    <form className="form-split-bill">
      <h2>SPLIT A WITH WITH $NAME</h2>

      <label>ðŸ’° Bill value</label>
      <input type="text" />

      <label>ðŸ¤£ Your expense</label>
      <input type="text" />

      <label>ðŸ¤— $Friend expense</label>
      <input type="text" disabled />

      <label>ðŸ‘¬ Who is paying the bill?</label>
      <select>
        <option value='user'>You</option>
        <option value='x'>Friend</option>
      </select>

      <Button>Split Bill</Button>
    </form>
  )
}
